    log(req.method)
    res.end('Send POST requests to /ephemeral please and thanks')
  })

  /**
   * Create the server and pass the req and res through the router
   */
  var app = http.createServer(function (req, res) {
    router(req, res, finalhandler(req, res))
  })

  return app
}
